#include <stdio.h>
void insertionsort(int *a,int n){
    int i,j,key;
    for (j=1;j<n;j++)
    {
        i=j-1;
        key=a[j];
        while(i>=0 && a[i]>key)
        {
            a[i+1]=a[i];
            i--;
        }
        a[i+1]=key;
    }  
}
